window.addEventListener("DOMContentLoaded",()=>{new class{constructor(e){this.slider=document.querySelector(e),this.blocksLength=this.slider.querySelectorAll(".slider__block").length,this.count=0,this.swipeRightDelay=!0,this.swipeLeftDelay=!0,this.activeItem=this.slider.querySelector(".slider__block_active"),this.swipeRightTrigger(),this.swipeLeftTrigger()}swipeRight(){if(this.count<this.blocksLength-1&&this.swipeRightDelay&&(setTimeout(()=>{this.swipeRightDelay=!0},250),this.swipeRightDelay)){let e=document.querySelector(".slider__block_active"),i=e.nextElementSibling;i.classList.remove("emergence-right"),i.classList.add("slider__block_active"),e.classList.remove("slider__block_active"),e.classList.add("emergence-left"),this.count++,this.count==this.blocksLength-1?this.slider.querySelector(".slider__arrow-right").classList.add("arrow-hidden"):this.slider.querySelector(".arrow-hidden")&&this.slider.querySelector(".slider__arrow-left").classList.remove("arrow-hidden"),this.swipeRightDelay=!1}}swipeLeft(){if(this.count>0&&this.swipeLeftDelay&&(setTimeout(()=>{this.swipeLeftDelay=!0},250),this.swipeLeftDelay)){let e=document.querySelector(".slider__block_active"),i=e.previousElementSibling;i.classList.remove("emergence-left"),i.classList.add("slider__block_active"),e.classList.remove("slider__block_active"),e.classList.add("emergence-right"),this.count--,0==this.count?this.slider.querySelector(".slider__arrow-left").classList.add("arrow-hidden"):this.slider.querySelector(".arrow-hidden")&&this.slider.querySelector(".slider__arrow-right").classList.remove("arrow-hidden"),this.swipeLeftDelay=!1}}swipeRightTrigger(){this.slider.querySelector(".slider__arrow-right").addEventListener("click",()=>{this.swipeRight()})}swipeLeftTrigger(){this.slider.querySelector(".slider__arrow-left").addEventListener("click",()=>{this.swipeLeft()})}}(".slider")});